import { usePersonStore } from '@/stores/PersonStore';
import { ref } from 'vue';
export default {
  setup() {
    const personStore = usePersonStore();
    const categoryName = ref('');
    const submitForm = async () => {
      if (categoryName.value.trim()) {
        await personStore.createPerson(categoryName.value);
        categoryName.value = '';
      }
    };
    return {
      categoryName,
      submitForm
    };
  }
};
